        AsyncStorage.getItem('visitedBefore',(visit) =>{
            if(visit !== null){
                navigation.navigate('/Login');
            } else {
                // i am new
                redirectTimer();
            }
        })
    },[]);
    const redirectTimer = () => {
        setTimeout(()=>{
            // set firstVisit
            AsyncStorage.setItem('visitedBefore',true);
            // redirect here
            navigation.navigate('/Login');
        },2000)
    }
    return (
        <View style={{flex:1}}>
            <Image style={styles.image} source={SplashPage}/>
        </View>

)
}

